<script setup>
import SearchBar from "@/Components/SearchBar.vue";
import Button from "@/Components/Button.vue";
import { PlusIcon } from "@/Components/Icons/solid";
import Modal from "@/Components/Modal.vue";
import AddTier from "@/Pages/LoyaltyProgramme/Partial/AddTier.vue";
import { ref, onMounted } from "vue";
import Table from "@/Components/Table.vue";
import axios from "axios";
const isModalOpen = ref(false);
const openModal = () => {
    isModalOpen.value = true;
};
const closeModal = () => {
    isModalOpen.value = false;
};

const columns = ref([
    { field: "icon", header: "Icon", width: "9", sortable: false },
    { field: "name", header: "Tier Name", width: "15", sortable: true },
    {
        field: "min_amount",
        header: "Amount spend to achive ",
        width: "24",
        sortable: true,
    },
    {
        field: "type_all",
        header: "Entry Rewards ",
        width: "29",
        sortable: true,
    },
    {
        field: "member",
        header: "Member ",
        width: "13",
        sortable: true,
    },
    {
        field: "action",
        header: "",
        width: "10",
        sortable: false,
        edit: true,
        delete: true,
    },
]);

const tier = ref([]);
const totalPages = ref(1);
const rowsPerPage = ref(8);

const rowType = {
    rowGroups: false,
    expandable: false,
    groupRowsBy: "name",
};
const actions = {
    view: (id) => `/loyalty-programme/tier_details/${id}`,
    replenish: (id) => `/loyalty-programme/tier_details/${id}`,
    // For 'grid' variant only has below two
    edit: (id) => `/loyalty-programme/tier_details/${id}/edit`,
    delete: (id) => `/loyalty-programme/products/${id}/delete`,
};

const getData = () => {
    axios
        .get("/loyalty-programme/getShowRecords")
        .then((response) => {
            tier.value = response.data;
            totalPages.value = Math.ceil(tier.value.length / rowsPerPage.value);
        })
        .catch((error) => {
            // If there's an error fetching data
            console.log("Error fetching data:", error); // Log the error to console
        });
};

onMounted(() => {
    getData();
});
</script>

